import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';


// Simulated API function
const RegistrationAPI = async (userData) => {
    // Simulate network delay
    return new Promise((resolve, reject) => {
        setTimeout(() => {
            // Simulate API response
            if (userData.username !== "exists") {  // Simulating a successful registration unless username is "exists"
                resolve({ ok: true });
            } else {
                reject({ ok: false, statusText: "Username already exists" });
            }
        }, 1000);
    });
};



function Register() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');
    const navigate = useNavigate();  // Create navigate function instance

    const handleSubmit = async (event) => {
        event.preventDefault();
        console.log(username, email, password);
        
        // Here you would normally handle registration
        // For example, calling an API to register the user
        try {
            const response = await RegistrationAPI({ username, email, password });  // Replace with actual API call
            if (response.ok) {
                // Navigate to login page or anywhere else post-registration
                navigate('/login');
            } else {
                // Handle errors if registration was unsuccessful
                console.error('Registration failed:', response.statusText);
            }
        } catch (error) {
            console.error('Error during registration:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Email:
                <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
            </label>
            <label>
                Username:
                <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
            </label>
            <label>
                Password:
                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
            </label>
            <button type="submit">Register</button>
        </form>
    );
}

export default Register;
