/*Login.js is a simple login form component that takes a username and password as input and submits the form to the server.*/

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { LoginAPI } from '../utils/Api';
import { UserContext } from './UserContext';

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();  // Initialize the navigate function

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await LoginAPI({ username, password });
            if (response.ok) {
                console.log('Login successful'); // Assuming the token is what you're interested in
                navigate('/'); // Navigate to home or dashboard on success
            } else {
                console.error('Login failed:', response.statusText); // Ensure this line is correctly accessing 'statusText'
                // Handle errors or invalid login attempts here
            }
        } catch (error) {
            console.error('Error during login:', error);
            // Handle unexpected errors here
        }
    };
    

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Username:
                <input
                   type="text"
                   value={username}
                   onChange={(e) => setUsername(e.target.value)}
    
/>
            </label>
            <label>
                Password:
                <input
                   type="password"
                   value={password}
                   onChange={(e) => setPassword(e.target.value)}
    
/>

            </label>
            <button type="submit">Login</button>
        </form>
    );
}

export default Login;
