import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { UserProvider } from './contexts/UserContext'; // Ensure the path is correct
import Home from './components/Home';
import Login from './components/Login';
import Register from './components/Register';
import Navbar from './components/Navbar';
import NewPost from './components/NewPost';
import PostsList from './components/Posts';



function App() {
    return (
        <Router>
            <UserProvider> {/* Wrap all components needing user context inside UserProvider */}
                <div>
                    <Navbar />
                    <Routes>
                        <Route path="/" element={<Home />} />
                        <Route path="/login" element={<Login />} />
                        <Route path="/register" element={<Register />} />
                        <Route path="/post" element={<PostsList />} /> 
                        <Route path="/create-post" element={<NewPost />} />  // Add this line
                    </Routes>
                </div>
            </UserProvider>
        </Router>
    );
}
export default App;
