// src/utils/Api.js
const users = [
    { username: "user1", password: "pass1", token: "token123" },
    { username: "user2", password: "pass2", token: "token456" }
];

const LoginAPI = async ({ username, password }) => {
    return new Promise((resolve, reject) => {
        setTimeout(() => {
            const user = users.find(u => u.username === username && u.password === password);
            if (user) {
                resolve({ ok: true, token: user.token });  // Simulate successful login with a token
            } else {
                reject({ ok: false, statusText: "Invalid username or password" });
            }
        }, 1000); // Simulate network delay
    });
};


// Simulated API function
const RegistrationAPI = async (userData) => {
    // Simulate network delay
    return new Promise((resolve, reject) => {
        setTimeout(() => {
            // Simulate API response
            if (userData.username !== "exists") {  // Simulating a successful registration unless username is "exists"
                resolve({ ok: true });
            } else {
                reject({ ok: false, statusText: "Username already exists" });
            }
        }, 1000);
    });
};

export { RegistrationAPI };
export { LoginAPI };
