import React, { useContext } from 'react'; // Import useContext if you are using context API for state management
import { Link } from 'react-router-dom';
import styles from './Navbar.module.css';
import { UserContext } from './UserContext'; // Assuming you have a context provider for user state

function Navbar() {
    const { user, setUser } = useContext(UserContext); // Using context to access user state

    const handleLogout = () => {
        // Logout logic here
        setUser(null); // Setting user to null after logout
    };

    return (
        <nav className={styles.navbar}>
            <ul className={styles.navList}>
                <li className={styles.navItem}><Link to="/">Home</Link></li>
                <li className={styles.navItem}><Link to="/posts">Posts</Link></li>
                {user ? (
                    <>
                        <li className={styles.navItem}><Link to="/create-post">Create Post</Link></li>
                        <li className={styles.navItem}><button onClick={handleLogout}>Logout</button></li>
                    </>
                ) : (
                    <>
                        <li className={styles.navItem}><Link to="/login">Login</Link></li>
                        <li className={styles.navItem}><Link to="/register">Register</Link></li>
                    </>
                )}
            </ul>
        </nav>
    );
}

export default Navbar;
