// Code for a simple login form that uses the LoginAPI function to authenticate the user.

import React, { useState, useContext } from 'react'; // Import useContext
import { useNavigate } from 'react-router-dom';
import { LoginAPI } from '../utils/Api';
import { UserContext } from '../contexts/UserContext'; 

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const { setUser } = useContext(UserContext);  // Destructure setUser from context
    const navigate = useNavigate();
    
    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await LoginAPI({ username, password });
            if (response.ok) {
                setUser({ token: response.token, ...response.user });  // Set the user context with token and user details
                console.log('Login successful');
                navigate('/'); // Navigate to home or dashboard on success
            } else {
                console.error('Login failed:', response.statusText);
                alert(response.statusText); // Display error message to the user
            }
        } catch (error) {
            console.error('Error during login:', error);
            alert('Login failed. Please try again.'); // Display a general error message to the user
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Username:
                <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
            </label>
            <label>
                Password:
                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
            </label>
            <button type="submit">Login</button>
        </form>
    );
}

export default Login;
