function Register() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');
    const [secretKey, setSecretKey] = useState(''); // State to hold the secret key input
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();
        
        try {
            const response = await RegistrationAPI({ username, email, password, secretKey });
            if (response.ok) {
                // Navigate to login page or anywhere else post-registration
                navigate('/login');
            } else {
                // Handle errors if registration was unsuccessful
                alert('Registration failed: ' + response.statusText);
            }
        } catch (error) {
            alert('Error during registration: ' + error.message);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Email:
                <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
            </label>
            <label>
                Username:
                <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
            </label>
            <label>
                Password:
                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
            </label>
            <label>
                Secret Key (Admins Only):
                <input type="text" value={secretKey} onChange={(e) => setSecretKey(e.target.value)} />
            </label>
            <button type="submit">Register</button>
            <p>Only admin users with a valid secret key can register and author this web app.</p>
        </form>
    );
}

export default Register;
