// NewPost component is a form that allows users to create new posts. The form has input fields for the title and content of the post. When the form is submitted, the handleSubmit function is called, which makes a POST request to the /api/posts endpoint with the post data. The post data is constructed from the title and content state variables using the useState hook.
import React, { useState } from 'react';

function NewPost() {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    const postData = { title, content, author_id: 1 }; // Assuming author_id is static for example

    try {
      const response = await fetch('http://localhost:3001/posts/api/', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(postData),
      });

      console.log('Full response:', response);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      console.log('Post created:', data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>Title:<input type="text" value={title} onChange={e => setTitle(e.target.value)} /></label>
      <label>Content:<textarea value={content} onChange={e => setContent(e.target.value)} /></label>
      <button type="submit">Create Post</button>
    </form>
  );
}

export default NewPost;
