// src/utils/Api.js
// Simulated API functions for registration and login


// src/utils/Api.js
const LoginAPI = async ({ username, password }) => {
    try {
        console.log({ username, password });  // Add this right before the fetch call in your login function

        const response = await fetch('http://localhost:3000/posts/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ username, password })
        });

        const data = await response.json();
        if (response.ok) {
            return { ok: true, token: data.accessToken };
        } else {
            return { ok: false, statusText: data.message || "Invalid username or password" };
        }
    } catch (error) {
        return { ok: false, statusText: "Network error" };
    }
};



// Simulated API function
const RegistrationAPI = async ({ username, email, password }) => {
    try {
        const response = await fetch('http://localhost:3000/posts/register', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ username, email, password })  // Include email in the payload
        });

        const data = await response.json();
        if (response.ok) {
            return { ok: true };
        } else {
            return { ok: false, statusText: data.message || "Username or email already exists" };
        }
    } catch (error) {
        return { ok: false, statusText: "Network error" };
    }
};




export { RegistrationAPI };
export { LoginAPI };
