/*Register.js is a new component that will be used to register new users. It will contain a form with fields for email, username, and password. When the form is submitted, it will make an API call to register the user. If the registration is successful, the user will be navigated to the login page.*/
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { RegistrationAPI } from '../utils/Api';


function Register() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');
    const navigate = useNavigate();  // Create navigate function instance

    const handleSubmit = async (event) => {
        event.preventDefault();
        
        
        // Here you would normally handle registration
        // For example, calling an API to register the user
        try {
            console.log(username, email, password);
            const response = await RegistrationAPI({ username, email, password });  // Replace with actual API call
            if (response.ok) {
                // Navigate to login page or anywhere else post-registration
                navigate('/login');
            } else {
                // Handle errors if registration was unsuccessful
                console.error('Registration failed:', response.statusText);
            }
        } catch (error) {
            console.error('Error during registration:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Email:
                <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
            </label>
            <label>
                Username:
                <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
            </label>
            <label>
                Password:
                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
            </label>
            <button type="submit">Register</button>
        </form>
    );
}

export default Register;
