/*Login.js is a simple login form component that takes a username and password as input and submits the form to the server.*/

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { LoginAPI } from '../utils/Api';

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();  // Initialize the navigate function

    const handleSubmit = async (event) => {
        event.preventDefault();
        console.log(username, password);

        // Example: Implement login functionality here
        // Assuming login API returns a status code of 200 on successful login
        
        try {
            // Simulate API call
            const response = await LoginAPI(username, password); // You need to implement this
            if (response.status === 200) {
                navigate('/'); // Navigate to home page or dashboard on success
            } else {
                console.error('Login failed:', response.status);
                // Handle errors or invalid login attempts here
            }
        } catch (error) {
            console.error('Error during login:', error);
        }
        
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Username:
                <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
            </label>
            <label>
                Password:
                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
            </label>
            <button type="submit">Login</button>
        </form>
    );
}

export default Login;
