// Posts component fetches a list of posts from the server and displays them on the page. 
// The posts are fetched from the /api/posts endpoint using the fetch API.
import React, { useEffect, useState } from 'react';

function PostsList() {
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        fetch('/api/posts')
            .then(response => response.json())
            .then(data => setPosts(data))
            .catch(error => console.error('Error fetching posts:', error));
    }, []);

    return (
        <div>
            {posts.map(post => (
                <div key={post.id}>
                    <h3>{post.title}</h3>
                    <p>{post.content}</p>
                    <small>{`Posted on ${new Date(post.created_at).toLocaleDateString()}`}</small>
                </div>
            ))}
        </div>
    );
}

export default PostsList;
